mapper.select:
SELECT REQUEST_NUM, MEMBER_ID, SUBJECT, R_CONTENT, PASSWORD, UPLOAD, REGDATE
FROM REQUEST
WHERE REQUEST_NUM = #{requestNum}

SELECT *
FROM (SELECT ROWNUM R_NUM, R.*
        FROM (SELECT *
              FROM REQUEST
              ORDER BY REQUEST_NUM
             ) R
     )    
WHERE R_NUM BETWEEN #{begin} AND #{end}
